// Get articles by category
export const getArticlesByCategory = (category: string): BlogArticle[] => {
  return BLOG_ARTICLES.filter(a => a.category === category);
};

// Get featured articles (first 6)
export const getFeaturedArticles = (): BlogArticle[] => {
  return BLOG_ARTICLES.slice(0, 6);
};

// Get all unique categories
export const getCategories = (): string[] => {
  return [...new Set(BLOG_ARTICLES.map(a => a.category))];
};
